//小思同学页面
<template>
  <div>
    <!-- 头部导航 -->
    <van-nav-bar title="小思同学" left-arrow @click-left="$router.push('/layout/user')" />
    <!-- 左边聊天 -->
    <div class="left">
      <img src="@/assets/logo.png" alt="">
      <div class="text">要说的1222222222222222222222222222222222222223</div>
    </div>
    <!-- 右边聊天 -->
    <div class="right">
      <div class="text">
        要说的话2144444444444444444444444444444444444444444444444
      </div>
      <img src="@/assets/logo.png" alt="">
    </div>
    <div class="left">
      <img src="@/assets/logo.png" alt="">
      <div class="text">要说的3321111111111111111111111111111111111111111</div>
    </div>
    <!-- 底部输入框 -->
    <van-cell-group>
      <van-field v-model="value" placeholder="说点什么...">
        <van-button slot="button" size="small">提交</van-button>
      </van-field>
    </van-cell-group>
  </div>
</template>

<script>
  export default {
    data() {
      return {
        value: ''
      }
    }
  }
</script>

<style lang="less" scoped>
  .van-nav-bar {
    background-color: #007bff;

    /deep/ .van-icon {
      color: #ffffff;
    }

    /deep/.van-nav-bar__title {
      color: white;
    }

    /deep/.van-cell {
      font-size: 16Px;
    }
  }

  /deep/.van-button--default {
    font-size: 15Px;
    border: none;
  }

  .van-cell-group {
    position: fixed;
    bottom: 0;
    width: 100%;
  }

  .left {
    float: left;
    width: 100%;
    margin-left: 5Px;
    margin-top: 20Px;

    img {
      width: 50Px;
      height: 50Px;
      border-radius: 100%;
      vertical-align: top;

    }

    .text {
      position: relative;
      display: inline-block;
      max-width: 60%;
      font-size: 14Px;
      margin-left: 15Px;
      background-color: #d7e5f5;
      word-break: break-all;
      padding: 8Px;
      border-radius: 8Px;
    }

    .text::before {
      content: '';
      width: 10Px;
      height: 10Px;
      background-color: #d7e5f5;
      position: absolute;
      left: -4Px;
      top: 10Px;
      transform: rotate(45deg);
    }
  }

  .right {
    float: right;
    width: 100%;
    padding-left: 20%;
    margin-right: 5Px;
    margin-top: 20Px;

    .text {
      position: relative;
      display: inline-block;
      max-width: 80%;
      font-size: 14Px;
      background-color: #d7e5f5;
      word-break: break-all;
      padding: 8Px;
      border-radius: 8Px;
      vertical-align: middle;

    }

    .text::before {
      content: '';
      width: 10Px;
      height: 10Px;
      background-color: #d7e5f5;
      position: absolute;
      right: -4Px;
      top: 10Px;
      transform: rotate(45deg);
    }

    img {
      float: right;
      width: 50Px;
      height: 50Px;
      border-radius: 100%;
      vertical-align: top;
    }
  }
</style>
