import Vue from 'vue'
import VueRouter from 'vue-router'
//导入组件
import Login from '@/views/login.vue'
import Home from '@/views/Home.vue'
Vue.use(VueRouter)

const routes = [
  {
    path: '/',
    redirect: '/login',
  },
  {
    path: '/login',
    component: Login,
  },
  {
    path: '/home',
    component: Home,
  },
]

const router = new VueRouter({
  routes,
})

router.beforeEach((to, from, next) => {
  if (to.path === 'login') {
    next()
  } else {
    if (localStorage.token === '') {
      next('/login')
    } else {
      next()
    }
  }
})

export default router
