//评论组件
<template>
  <div>
    <!-- 评论列表 -->
    <div class="cmt-list">
      <!-- 评论的item项 -->
      <div class="cmt-item">
        <div class="cmt-header">
          <div class="cmt-header-left">
            <img src="" alt="" class="avatar">
            <span class="uname">zs</span>
          </div>
          <div class="cmt-header-right">
            <van-icon name="like" size="16" color="red"></van-icon>
            <van-icon name="like-o" size="16" color="gray"></van-icon>
          </div>
        </div>
        <!-- 主体部分 -->
        <div class="cmt-body">
          1222222222222222222442141433333333333333333333333333333231
        </div>
        <!-- 尾部部分 -->
        <div class="cmt-footer">3天谴</div>
      </div>
    </div>
  </div>
</template>

<script>
  import {
    commentsAPI
  } from "@/api/index.js"
  export default {
    props: {
      id: Number
    },
    async created() {
      const res = await commentsAPI({
        id: this.id
      })
    }
  }
</script>

<style lang="less" scoped>
  .cmt-list {
    padding: 10px;

    .cmt-item {
      padding: 15Px 0;

      +.cmt-item {
        border-top: 1px solid #f8f8f8;
      }

      .cmt-header {
        display: flex;
        align-items: center;
        justify-content: space-between;

        .cmt-header-left {
          display: flex;
          align-items: center;

          .avatar {
            width: 40Px;
            height: 40Px;
            background-color: #f8f8f8;
            border-radius: 50%;
            margin-right: 15Px;
          }

          .uname {
            font-size: 12Px;
          }
        }
      }

      .cmt-body {
        font-size: 14Px;
        line-height: 28Px;
        text-indent: 2em;
        margin-top: 6Px;
        word-break: break-all;
      }

      .cmt-footer {
        font-size: 12Px;
        color: gray;
        margin-top: 10Px;
      }
    }
  }
</style>
