// 搜索页面
<template>
  <div>
    <!-- 搜索头部 -->
    <div class="search-header" style="height: 46px">
      <van-icon name="arrow-left" color="white" size="18px" class="goback" @click="$router.back()"
        style="padding-left: 14px" />
      <van-search placeholder="请输入关键词" background="#007bff" shape="round" v-fofo @input="inputFn" v-model="kw" />
    </div>
    <!-- 搜索关键词联想列表 -->
    <div class="sugg-list">
      <div class="sugg-item" v-for="(item,index) in keywordList" :key="index" v-if="kw.length!==0"
        v-html="lightFn(item, kw)">
      </div>
    </div>
    <!-- 搜索历史 -->
    <div class="search-history" v-show="kw.length===0">
      <!-- 标题 -->
      <van-cell title="搜索历史">
        <template #right-icon>
          <van-icon name="delete" class="search-icon"></van-icon>
        </template>
      </van-cell>
      <!-- 历史列表 -->
      <div class="history-list">
        <span class="history-item">历史文学</span>
      </div>
    </div>
  </div>
</template>

<script>
  import {
    suggestListAPI
  } from "@/api/index.js"
  export default {
    data() {
      return {
        kw: '', //搜索关键词
        timer: null, //定时器
        keywordList: [] //关键词联想数组
      }
    },
    methods: {
      //搜索框输入方法
      inputFn() {
        //防抖(设置定时器，然后每次触发时清除定时器)
        clearTimeout(this.timer)

        this.timer = setTimeout(async () => {
          if (this.kw.length === 0) return

          const {
            data: res
          } = await suggestListAPI({
            keyword: this.kw
          })
          this.keywordList = res.data.options
        }, 300)
      },
      //处理关键词高亮
      lightFn(str, target) {
        //str是原来的字符串
        //target是关键字
        //变量不能直接放入正则表达式中，需要新建正则实例
        let reg = new RegExp(target, 'ig')
        return str.replaceAll(reg, (match) => {
          return `<span style="color:red;">${match}</span>`
        })
      }
    },
  }
</script>

<style lang="less" scoped>
  .search-header {
    display: flex;
    align-items: center;
    background-color: #007bff;
    overflow: hidden;

    .van-search {
      flex: 1;
    }
  }

  .sugg-list {
    .sugg-item {
      padding: 0 15Px;
      border-bottom: 1Px solid #f8f8f8;
      font-size: 14Px;
      line-height: 50Px;
      white-space: nowrap;
      overflow: hidden;
      text-overflow: ellipsis;
    }
  }

  .search-icon {
    font-size: 16Px;
    line-height: inherit;
  }

  .history-list {
    padding: 0 10Px;

    .history-item {
      display: inline-block;
      font-size: 12Px;
      padding: 8Px 14Px;
      background-color: #efefef;
      margin: 10Px 8Px 0Px 8Px;
      border-radius: 10Px;
    }
  }
</style>
