//基于axios封装网络请求
import theAxios from 'axios'
const axios = theAxios.create({
  baseURL: 'http://toutiao.itheima.net/',
  timeout: 20000, //20s无响应直接判定超时
})
export default ({
  url,
  method = 'GET',
  params = {},
  data = {},
  headers = {},
}) => {
  return axios({
    url,
    method,
    params,
    data,
    headers,
  })
}
