//这个是搜索结果的详情页
<template>
  <div>
    <!-- 搜索结果导航栏 -->
    <div class="search-result-container">
      <!-- 点击实现后退效果 -->
      <van-nav-bar title="搜索结果" left-arrow @click-left="$router.go(-1)" fixed style="background-color:#007bff">
      </van-nav-bar>
    </div>
    <van-list v-model="loading" :finished="finished" finished-text="没有更多了" @load="onLoad">

      <ArticleItem v-for="item in resultList" :key="item.art_id" :article="item" :isShow="false"></ArticleItem>
    </van-list>

  </div>
</template>

<script>
  import {
    searchResultAPI
  } from "@/api/index.js"
  import ArticleItem from "@/components/ArticleItem.vue"
  export default {
    data() {
      return {
        page: 1,
        resultList: [],
        loading: false,
        finished: false
      }
    },
    components: {
      ArticleItem
    },
    async created() {
      const res = await searchResultAPI({
        page: this.page,
        q: this.$route.params.kw
      })
      this.resultList = res.data.data.results
    },
    methods: {
      onLoad() {
        this.page++
        // 异步更新数据
        setTimeout(async () => {
          const res = await searchResultAPI({
            page: this.page,
            q: this.$route.params.kw
          })
          // 加载状态结束
          this.loading = false;

          // 数据全部加载完成
          if (this.list.length >= 40) {
            this.finished = true;
          }
        }, 500);
      }
    }
  }
</script>

<style lang="less" scoped>
  .search-result-container {
    padding-top: 46Px;
  }

  .van-nav-bar {
    background-color: #007bff;


  }

  /deep/.van-nav-bar .van-icon {
    color: white;
