//小思同学页面
<template>
  <div>
    <!-- 头部导航 -->
    <van-nav-bar title="小思同学" left-arrow @click-left="$router.push('/layout/user')" />
    <!-- 左边聊天 -->
    <div class="left">
      <img src="@/assets/logo.png" alt="">
      <div class="text">要说的话13141123123123123123123</div>
    </div>
    <!-- 右边聊天 -->
    <div class="right">
      <div class="text">要说的话12312312312321312312313123</div>
      <img src="@/assets/logo.png" alt="">
    </div>

    <div class="left">
      <img src="@/assets/logo.png" alt="">
      <div class="text">要说的话</div>
    </div>
    <!-- 底部输入框 -->
    <van-cell-group>
      <van-field v-model="value" placeholder="说点什么...">
        <van-button slot="button" size="small">提交</van-button>
      </van-field>
    </van-cell-group>
  </div>
</template>

<script>
  export default {
    data() {
      return {
        value: ''
      }
    }
  }
</script>

<style lang="less" scoped>
  .van-nav-bar {
    background-color: #007bff;

    /deep/ .van-icon {
      color: #ffffff;
    }

    /deep/.van-nav-bar__title {
      color: white;
    }

    /deep/.van-cell {
      font-size: 16Px;
    }
  }

  /deep/.van-button--default {
    font-size: 15Px;
    border: none;
  }

  .van-cell-group {
    position: fixed;
    bottom: 0;
    width: 100%;
  }

  .left {
    width: 100%;
    padding-right: 30%;
    margin-top: 10Px;
    margin-left: 5Px;

    img {
      float: left;
      width: 50Px;
      height: 50Px;
      border-radius: 100%;
    }

    .text {
      float: right;
      display: inline-block;
      position: relative;
      left: 50Px;
      margin-left: 10PX;
      font-size: 16Px;
      padding: 8Px;
      border-radius: 5PX;
      background-color: #cae0f7;
      word-wrap: break-word;
      word-break: break-all;
    }

    .text::before {
      content: '';
      width: 10PX;
      height: 10Px;
      background-color: #cae0f7;
      z-index: -1;
      position: absolute;
      top: 11Px;
      left: -4PX;
      transform: rotate(45deg);
    }
  }

  .right {
    width: 100%;
    padding-left: 30%;
    margin-top: 10Px;
    padding-right: 5PX;

    img {
      position: absolute;
      right: 0;
      width: 50Px;
      height: 50Px;
      border-radius: 100%;
    }

    .text {
      position: absolute;
      display: inline-block;
      right: 50Px;
      margin-right: 10PX;
      font-size: 16Px;
      padding: 8Px;
      border-radius: 5PX;
      background-color: #cae0f7;
    }

    .text::before {
      content: '';
      width: 10PX;
      height: 10Px;
      background-color: #cae0f7;
      z-index: -1;
      position: absolute;
      top: 11Px;
      right: -4PX;
      transform: rotate(45deg);
    }
  }
</style>
