//搜索页面
<template>
  <div>
    <div class="search-header" style="height: 46px">
      <van-icon name="arrow-left" color="white" size="18px" class="goback" @click="$router.back()"
        style="padding-left: 14px" />
      <van-search placeholder="请输入关键词" background="#007bff" shape="round" v-fofo @input="inputFn" v-model="kw" />
    </div>
    <div class="sugg-list">
      <div class="sugg-item">搜索建议</div>
    </div>
  </div>
</template>

<script>
  import {
    suggestListAPI
  } from "@/api/index.js"
  export default {
    data() {
      return {
        kw: '', //搜索关键词
        timer: null, //定时器
        keywordList: []
      }
    },
    methods: {
      //搜索框输入方法
      inputFn() {
        //防抖(设置定时器，然后每次触发时清除定时器)
        clearTimeout(this.timer)
        this.timer = setTimeout(async () => {
          if (this.kw.length === 0) return
          const {
            data: res
          } = await suggestListAPI({
            keyword: this.kw
          })
          console.log(res.data);
          this.keywordList = res.data.options
        }, 300)
      },
    },
  }
</script>

<style lang="less" scoped>
  .search-header {
    display: flex;
    align-items: center;
    background-color: #007bff;
    overflow: hidden;

    .van-search {
      flex: 1;
    }
  }

  .sugg-list {
    .sugg-item {
      padding: 0 15Px;
      border-bottom: 1Px solid #f8f8f8;
      font-size: 14Px;
      line-height: 50Px;
      white-space: nowrap;
      overflow: hidden;
      text-overflow: ellipsis;
    }
  }
</style>
