//小思同学页面
<template>
  <div>
    <!-- 头部导航 -->
    <van-nav-bar title="小思同学" left-arrow @click-left="$router.push('/layout/user')" />
    <!-- 左边聊天 -->
    <div class="left">
      <img src="@/assets/logo.png" alt="">
      <div class="text">要说的话</div>
    </div>
    <!-- 底部输入框 -->
    <van-cell-group>
      <van-field v-model="value" placeholder="说点什么...">
        <van-button slot="button" size="small">提交</van-button>
      </van-field>
    </van-cell-group>
  </div>
</template>

<script>
  export default {
    data() {
      return {
        value: ''
      }
    }
  }
</script>

<style lang="less" scoped>
  .van-nav-bar {
    background-color: #007bff;

    /deep/ .van-icon {
      color: #ffffff;
    }

    /deep/.van-nav-bar__title {
      color: white;
    }

    /deep/.van-cell {
      font-size: 16Px;
    }
  }

  /deep/.van-button--default {
    font-size: 15Px;
    border: none;
  }

  .van-cell-group {
    position: fixed;
    bottom: 0;
    width: 100%;
  }

  .left {
    float: left;
    margin-top: 10Px;
    margin-left: 5Px;

    img {
      width: 50Px;
      height: 50Px;
      border-radius: 100%;
    }

    .text {
      position: relative;
      display: inline-block;
      margin-left: 10PX;
      font-size: 16Px;
      padding: 8Px;
      border-radius: 5PX;
      background-color: #cae0f7;
    }

    .text::before {
      content: '';
      width: 10PX;
      height: 15Px;
      background-color: black;
      z-index: -1;
      position: absolute;
      top: 10Px;
      left: -8PX;
      transform: rotate(45deg);
    }
  }
</style>
