//评论组件
<template>
  <div>
    <!-- 评论列表 -->
    <div class="cmt-list">
      <!-- 评论的item项 -->
      <div class="cmt-item" v-for="item in commentList" :key="item.com_id">
        <div class="cmt-header">
          <div class="cmt-header-left">
            <img :src="item.aut_photo" alt="" class="avatar">
            <span class="uname">{{item.aut_name}}</span>
          </div>
          <div class="cmt-header-right">
            <van-icon name="like" size="16" color="red" v-if="item.is_liking"></van-icon>
            <van-icon name="like-o" size="16" color="gray" v-else></van-icon>
          </div>
        </div>
        <!-- 主体部分 -->
        <div class="cmt-body">
          {{item.content}}
        </div>
        <!-- 尾部部分 -->
        <div class="cmt-footer">{{timer(item.pubdate)}}</div>
      </div>
    </div>
  </div>
</template>

<script>
  //引入格式化时间方法
  import {
    timeAgo
  } from '@/utils/date.js';
  //引入评论接口
  import {
    commentsAPI,
    commentsLikeAPI
  } from "@/api/index.js"

  export default {
    data() {
      return {
        offset: null, //偏移量评论id
        commentList: []
      }
    },
    async created() {
      const res = await commentsAPI({
        id: this.$route.query.art_id
      })
      this.commentList = res.data.data.results
    },
    methods: {
      //这个是格式化时间的方法
      timer: timeAgo
    }
  }
</script>

<style lang="less" scoped>
  .cmt-list {
    padding: 10px;

    .cmt-item {
      padding: 15Px 0;

      +.cmt-item {
        border-top: 1px solid #f8f8f8;
      }

      .cmt-header {
        display: flex;
        align-items: center;
        justify-content: space-between;

        .cmt-header-left {
          display: flex;
          align-items: center;

          .avatar {
            width: 40Px;
            height: 40Px;
            background-color: #f8f8f8;
            border-radius: 50%;
            margin-right: 15Px;
          }

          .uname {
            font-size: 12Px;
          }
        }
      }

      .cmt-body {
        font-size: 14Px;
        line-height: 28Px;
        text-indent: 2em;
        margin-top: 6Px;
        word-break: break-all;
      }

      .cmt-footer {
        font-size: 12Px;
        color: gray;
        margin-top: 10Px;
      }
    }
  }
</style>
