//存储请求函数模块
//每个方法负责请求一个方法
import request from '@/utils/request.js'
//获取所以频道
export const getAllChannelsAPI = () => {
  return request({
    url: '/v1_0/channels',
  })
}
//登录接口
export const loginAPI = () => {
  return request({
    url: ' /v1_0/user/profile',
    method: POST,
  })
}
