//搜索页面
<template>
  <div>
    <div class="search-header">
      <van-icon name="arrow-left" color="white" size="18px" class="goback" @click="$router.back()" />
      <van-search placeholder="请输入关键词" background="#007bff" shape="round" v-fofo @input="inputFn" v-model="kw" />
      <div class="sugg-list">
        <div class="sugg-item">搜索建议</div>
      </div>
    </div>
  </div>
</template>

<script>
  export default {
    data() {
      return {
        kw: '', //搜索关键词
        timer: null //定时器
      }
    },
    methods: {
      //搜索框输入方法
      inputFn() {
        //防抖(设置定时器，然后每次触发时清除定时器)
        clearTimeout(this.timer)
        this.timer = setTimeout(() => {
          if (this.kw.length === 0) return

        }, 300)
      }
    }
  }
</script>

<style lang="less" scoped>
  .search-header {
    height: 46Px;
    display: flex;
    align-items: center;
    background-color: #007bff;
    overflow: hidden;

    .goback {
      padding-left: 14Px;
    }

    .van-search {
      flex: 1;
    }
  }
</style>
