//用户编辑资料页面
<template>
  <div>
    <!-- 头部导航 -->
    <van-nav-bar title="编辑资料" left-arrow @click-left="$router.push('/layout/user')" />
    <!-- 编辑区域 -->
    <van-cell title="头像" is-link center>
      <template #default>
        <label for="imgChange">
          <van-image round class="avatar" :src="photo"></van-image>
        </label>
        <input type="file" name="imgChange" id="imgChange" v-show="false" @change="imgChange">
      </template>
    </van-cell>
    <van-cell title="名称" is-link :value="user.name" />
    <van-cell title="性别" is-link :value="user.gender===1? '女':'男'" />
    <van-cell title="生日" is-link :value="user.birthday" />
  </div>
</template>

<script>
  import {
    getUserAPI,
    changeImgAPI,
    changeUserAPI
  } from "@/api/index.js"

  export default {
    data() {
      return {
        user: {}, //个人信息
        photo: '', //修改图片后的新地址
      }
    },
    async created() {
      const res = await getUserAPI()
      this.user = res.data.data
      this.photo = res.data.data.photo
    },
    methods: {
      //修改头像方法
      async imgChange(e) {
        //将这个文件的地址传入后端
        //1.创建Formdata对象
        let theFd = new FormData()
        theFd.append('photo', e.target.files[0]) //2.将图片数据推送到新建的表单对象中
        //3.发起请求
        const res = await changeImgAPI(theFd)
        this.photo = res.data.data.photo
      },
      //修改用户资料方法


    }
  }
</script>

<style lang="less" scoped>
  .van-nav-bar {
    background-color: #007bff;

    /deep/ .van-icon {
      color: #ffffff;
    }

    /deep/.van-nav-bar__title {
      color: white;
    }

    /deep/.van-cell {
      font-size: 16Px;
    }
  }

  .avatar {
    width: 50Px;
    height: 50Px;
  }
</style>
