//小思同学页面
<template>
  <div>
    <!-- 头部导航 -->
    <van-nav-bar title="小思同学" left-arrow @click-left="$router.push('/layout/user')" />
    <!-- 左边聊天 -->
    <div class="left">
      <img src="@/assets/logo.png" alt="">
      <div class="text">要说的话</div>
    </div>
    <!-- 底部输入框 -->
    <van-cell-group>
      <van-field v-model="value" placeholder="说点什么...">
        <van-button slot="button" size="small">提交</van-button>
      </van-field>
    </van-cell-group>
  </div>
</template>

<script>
  export default {
    data() {
      return {
        value: ''
      }
    }
  }
</script>

<style lang="less" scoped>
  .van-nav-bar {
    background-color: #007bff;

    /deep/ .van-icon {
      color: #ffffff;
    }

    /deep/.van-nav-bar__title {
      color: white;
    }

    /deep/.van-cell {
      font-size: 16Px;
    }
  }

  /deep/.van-button--default {
    font-size: 15Px;
    border: none;
  }

  .van-cell-group {
    position: fixed;
    bottom: 0;
    width: 100%;
  }

  .left {
    float: left;
    margin-top: 10Px;
    margin-left: 5Px;

    img {
      width: 50Px;
      height: 50Px;
      border-radius: 100%;
    }

    .text {
      display: inline-block;
      margin-left: 10PX;
      font-size: 16Px;
      padding: 8Px;
      border-radius: 5PX;
      background-color: #cae0f7;
    }

    .text::before {
      //这里就是小三角了
      content: "";
      display: block;
      position: absolute;
      left: -20px; //给小三角定位，更具实际需求调整
      top: 75px; //给小三角定位，更具实际需求调整
      border-width: 10px;
      border-style: dashed solid dashed dashed; //四个参数分别代表向下，左，上右的小三角(对应方向的小三角设置为solid，其他的为dashed。我这里演示向左的小三角，所以设置第二个）
      border-color: transparent black transparent transparent; //四个参数分别代表向下，左，上右的小三角。对应方向的小三角设置你想要的颜色（为了直观我这里设置黑色），其他的为transparent。我这里演示向左的小三角，所以设置第二个。
    }

