//搜索页面
<template>
  <div class="search-header">
    <van-icon name="arrow-left" color="white" size="18px" class="goback" @click="$router.back()" />
    <van-search placeholder="请输入关键词" background="#007bff" shape="round" v-fofo @input="inputFn" v-model="kw" />
  </div>
</template>

<script>
  export default {
    data() {
      return {
        kw: '', //搜索关键词
        timer: null
      }
    },
    methods: {
      //搜索框输入方法
      inputFn() {
        //防抖
        clearTimeout(this.timer)
        this.timer = setTimeout(() => {
          this.kw
        }, 300)
      }
    }
  }
</script>

<style lang="less" scoped>
  .search-header {
    height: 46Px;
    display: flex;
    align-items: center;
    background-color: #007bff;
    overflow: hidden;

    .goback {
      padding-left: 14Px;
    }

    .van-search {
      flex: 1;
    }
  }
</style>
